import { useState } from "react";

const products = [
  { id: 1, name: "Cargador Rápido", price: 15000 },
  { id: 2, name: "Auriculares Bluetooth", price: 25000 },
  { id: 3, name: "Funda Silicona", price: 8000 },
];

export default function Accessories() {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => setCart([...cart, product]);
  
  // Función para borrar todo del carrito
  const clearCart = () => setCart([]);

  return (
    <section className="py-20 text-center">
      <h2 className="text-4xl font-bold mb-6">Accesorios</h2>

      <div className="max-w-4xl mx-auto grid md:grid-cols-3 gap-6 px-4">
        {products.map(p => (
          <div key={p.id} className="bg-gray-800 p-6 rounded shadow hover:shadow-lg transition">
            <h3 className="text-xl font-semibold mb-2">{p.name}</h3>
            <p className="mb-4">${p.price}</p>
            <button
              onClick={() => addToCart(p)}
              className="bg-blue-400 hover:bg-purple-500 text-gray-900 px-4 py-2 rounded font-semibold transition"
            >
              Añadir al carrito
            </button>
          </div>
        ))}
      </div>

      <div className="mt-10">
        <h3 className="text-2xl font-bold mb-2">Carrito:</h3>
        {cart.length === 0 ? <p>Está vacío</p> :
          <ul>
            {cart.map((item, idx) => <li key={idx}>{item.name} - ${item.price}</li>)}
          </ul>
        }

        {/* Botón para borrar todo el carrito */}
        {cart.length > 0 && (
          <button
            onClick={clearCart}
            className="mt-4 bg-red-500 hover:bg-red-700 text-white px-4 py-2 rounded font-semibold transition"
          >
            Borrar selección
          </button>
        )}
      </div>
    </section>
  );
}
