import { useState } from "react";

const products = [
  { id: 1, name: "Cargador Rápido", price: 15000 },
  { id: 2, name: "Auriculares Bluetooth", price: 25000 },
  { id: 3, name: "Funda Silicona", price: 8000 },
];

export default function Accessories() {
  // Guardar productos en el carrito con cantidad
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    const existing = cart.find(item => item.id === product.id);
    if (existing) {
      // Aumentar cantidad si ya está en el carrito
      setCart(cart.map(item => item.id === product.id ? {...item, qty: item.qty + 1} : item));
    } else {
      setCart([...cart, {...product, qty: 1}]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter(item => item.id !== id));
  };

  const clearCart = () => setCart([]);

  // Calcular total del carrito
  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  return (
    <section className="py-20 text-center">
      <h2 className="text-4xl font-bold mb-6">Accesorios</h2>

      <div className="max-w-4xl mx-auto grid md:grid-cols-3 gap-6 px-4">
        {products.map(p => (
          <div key={p.id} className="bg-gray-800 p-6 rounded shadow hover:shadow-lg transition">
            <h3 className="text-xl font-semibold mb-2">{p.name}</h3>
            <p className="mb-4">${p.price}</p>
            <button
              onClick={() => addToCart(p)}
              className="bg-blue-400 hover:bg-purple-500 text-gray-900 px-4 py-2 rounded font-semibold transition"
            >
              Añadir al carrito
            </button>
          </div>
        ))}
      </div>

      <div className="mt-10">
        <h3 className="text-2xl font-bold mb-2">Carrito:</h3>
        {cart.length === 0 ? <p>Está vacío</p> :
          <>
            <ul>
              {cart.map((item) => (
                <li key={item.id} className="flex justify-between items-center mb-2">
                  <span>{item.name} x {item.qty} - ${item.price * item.qty}</span>
                  <button 
                    onClick={() => removeFromCart(item.id)}
                    className="bg-red-500 hover:bg-red-700 text-white px-2 py-1 rounded ml-4"
                  >
                    ❌
                  </button>
                </li>
              ))}
            </ul>
            <p className="mt-2 font-bold text-lg">Total: ${total}</p>
            <button
              onClick={clearCart}
              className="mt-4 bg-red-500 hover:bg-red-700 text-white px-4 py-2 rounded font-semibold transition"
            >
              Borrar selección
            </button>
          </>
        }
      </div>
    </section>
  );
}
