import { useState } from "react";

export default function RepairForm() {
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    e.target.reset();
  };

  return (
    <section className="py-20 text-center">
      <h2 className="text-4xl font-bold mb-6">Solicitar Reparación</h2>
      {submitted && <p className="mb-4 text-green-400">¡Solicitud enviada!</p>}
      <form onSubmit={handleSubmit} className="max-w-md mx-auto flex flex-col gap-4">
        <input type="text" name="name" placeholder="Nombre" className="p-3 rounded border border-gray-700 bg-gray-900 text-white" required />
        <input type="text" name="model" placeholder="Modelo del celular" className="p-3 rounded border border-gray-700 bg-gray-900 text-white" required />
        <select name="issue" className="p-3 rounded border border-gray-700 bg-gray-900 text-white" required>
          <option value="">Selecciona el problema</option>
          <option value="pantalla">Pantalla</option>
          <option value="bateria">Batería</option>
          <option value="otro">Otro</option>
        </select>
        <button type="submit" className="bg-purple-500 hover:bg-blue-400 text-gray-900 p-3 rounded font-semibold transition">Enviar Solicitud</button>
      </form>
    </section>
  );
}
